/**
 * JSS color helper.
 */
export function rgb (r: number, g: number, b: number): string;




/**
 * JSS color helper (with alpha).
 */
export function rgba (
    r: number,
    g: number,
    b: number,
    a: number
): string;




/**
 * JSS url helper.
 */
export function url (x: string): string;
